import styled from 'styled-components';

const CountLike = styled.span`
width: 20px;
height: 20px;
display: flex;
align-items: center;
justify-content: center;
font-size: 20px;
background-color: #fff;
border: 0;
padding: 0;
cursor: pointer;
outline: 0;
border-radius: 100%;
.animation {
  pointer-events: none;
}
`;


export default CountLike;
