import styled from 'styled-components';

const CountLike = styled.span`
width:50px;
height: 50px;
display: flex;
align-items: left;
justify-content: left;
font-size: 30px;
background: #FD0F5E;
color: #FFF;
border: 0;
padding: 0;
cursor: pointer;
outline: 0;
border-radius: 100%;
.animation {
  pointer-events: none;
}
`;


export default CountLike;
