import styled from 'styled-components';

const CountLike = styled.span`
width:50px;
height: 50px;
display: flex;
align-items: center;
justify-content: center;
font-size: 30px;
background: #FD0F5E;
color: #FFF;
outline: 0;
border-radius: 100%;
`;


export default CountLike;
